// Puxar os dados do n√≥ correto do Google Sheets
const sheetData = $('Google Sheets - Ler Dados Instagram').first().json;

// Array para armazenar erros
let errors = [];

// Verificar se o campo "Tipo de m√≠dia" est√° preenchido e √© v√°lido
const validMediaTypes = ["FEED", "STORIES", "REELS"];
let mediaType = sheetData["Tipo de m√≠dia"]?.trim().toUpperCase();
if (!validMediaTypes.includes(mediaType)) {
  errors.push(`O campo "Tipo de m√≠dia" deve ser preenchido com uma das op√ß√µes: FEED, STORIES ou REELS.`);
}

// Valida√ß√£o de "Tema" e "Descri√ß√£o"
let keyword = sheetData["Tema"]?.trim() || "";
let description = sheetData["Descri√ß√£o"]?.trim() || "";
if (!description && !keyword) {
  errors.push("Se o campo 'Descri√ß√£o' n√£o estiver preenchido, o campo 'Tema' deve ser preenchido.");
}

// Campos principais
let imageBank = sheetData["Banco de imagem"]?.trim() || "";
let media = sheetData["M√≠dias"]?.trim() || "";

// Para FEED, pelo menos um dos campos de m√≠dia deve estar preenchido
if (mediaType === "FEED" && !imageBank && !media) {
  errors.push("O campo 'Banco de imagem' ou 'M√≠dias' √© obrigat√≥rio para o tipo de m√≠dia FEED.");
}

// Para STORIES, pelo menos um dos campos deve estar preenchido
if (mediaType === "STORIES" && !imageBank && !media) {
  errors.push("O tipo de m√≠dia STORIES deve ter pelo menos um dos seguintes campos preenchidos: 'Banco de imagem' ou 'M√≠dias'.");
}

// Lista de bancos que s√£o fontes v√°lidas de v√≠deo para REELS
const validVideoBanksForReels = ["Google Drive", "ImageKit", "URL"];

// Valida√ß√£o espec√≠fica para REELS
if (mediaType === "REELS") {
  const isValidVideoBank = validVideoBanksForReels.includes(imageBank);

  if (!media) {
    errors.push("O campo 'M√≠dias' deve ser preenchido para o tipo de m√≠dia REELS.");
  }

  if (imageBank && !isValidVideoBank) {
    errors.push(`O campo 'Banco de imagem' deve estar vazio para o tipo de m√≠dia REELS, exceto se for uma fonte de v√≠deo compat√≠vel como: ${validVideoBanksForReels.join(", ")}.`);
  }

  if (!isValidVideoBank && media && !media.match(/^https?:\/\/.*\.mp4$/)) {
    errors.push("O campo 'M√≠dias' deve conter uma URL .mp4 v√°lida para o tipo de m√≠dia REELS.");
  }

  // Valida√ß√£o do campo "Reels no Feed"
  let reelsInFeed = sheetData["Reels no Feed"]?.trim().toLowerCase();
  if (!["sim", "n√£o"].includes(reelsInFeed)) {
    errors.push("O campo 'Reels no Feed' deve ser 'Sim' ou 'N√£o' quando o tipo de m√≠dia for REELS.");
  }
}

// Valida√ß√£o de IA manual que exige M√≠dias (prompt ou input)
const imageBanksThatRequireMedia = [
  "GPT image", "Flux", "Freepik", "ImageKit",
  "Leonardo.Ai", "Midjourney", "Pollinations",
  "Google Drive"
];

const isManualIA = imageBanksThatRequireMedia.includes(imageBank) && !imageBank.toLowerCase().includes("autom√°tico");

if (isManualIA && !media) {
  errors.push(`O campo 'M√≠dias' deve ser preenchido quando o 'Banco de imagem' for '${imageBank}'.`);
}

// Se houver erros, lan√ßar exce√ß√£o
if (errors.length > 0) {
  throw new Error(errors.join("\n"));
}

// Retornar os dados validados
return sheetData;

// Tenta pegar os valores do JSON, garantindo que n√£o sejam undefined
const data = $('Google Sheets - Ler Dados Instagram').first().json.Data || "";
const hora = $('Google Sheets - Ler Dados Instagram').first().json.Hora || "";

// Se os valores estiverem vazios, gera um erro para debug
if (!data || !hora) {
  throw new Error(`Erro: Data ou Hora est√£o vazias. Data: '${data}', Hora: '${hora}'. Verifique o Google Sheets.`);
}

// Converte para formato ISO 8601 (necess√°rio para o Wait Node)
const [dia, mes, ano] = data.split("/");
const formattedDate = `${ano}-${mes}-${dia}T${hora}:00`;

// Retorna o valor formatado corretamente
return [{ dateTime: formattedDate }];

