// Entrada do node code
const inputData = $node["AI Agent - Vendedor"].json["output"];

// Fun√ß√£o para converter Markdown em formata√ß√£o WhatsApp
function convertMarkdownToWhatsApp(markdown) {
    let output = markdown;

    // Converter **negrito** para *negrito*
    output = output.replace(/\*\*(.*?)\*\*/g, '*$1*');

    // Remover links, mantendo apenas o texto ou adaptando
    output = output.replace(/\[(.*?)\]\((.*?)\)/g, '$1 ($2)');

    // Remover imagens no formato ![alt](URL)
    output = output.replace(/!\[.*?\]\(.*?\)/g, '');

    return output;
}

// Verificar se inputData √© um array ou string
let formattedOutput;

if (Array.isArray(inputData)) {
    // Se for um array, processar cada item
    formattedOutput = inputData.map(item => ({
        output: convertMarkdownToWhatsApp(item.output)
    }));
} else if (typeof inputData === 'string') {
    // Se for uma string, processar diretamente
    formattedOutput = {
        output: convertMarkdownToWhatsApp(inputData)
    };
} else {
    throw new Error('Formato inesperado de inputData. Deve ser uma string ou um array.');
}

// Retornar o resultado
return formattedOutput;
